import React from 'react'
import styled from 'styled-components'
import Navbar from './Navbar';
import Cartt from './Cartt';

function Home({basket,setBasket,image,price,rating,title}) {
  return (
    <Container>
    <Navbar/>
    <Banner>
        <img src="./img/im15.jpg" alt="" />
        <img src="./img/e4.jpg"/>
        
    </Banner>

    <Main>
        <Cartt basket={basket} setBasket={setBasket} 
        image={"image"}
        price={8900}
        rating={3}
        title={"Rosemary Mint"}
        />

        <Cartt basket={basket} setBasket={setBasket} />
        <Cartt basket={basket} setBasket={setBasket} />
        <Cartt basket={basket} setBasket={setBasket} />
        <Cartt basket={basket} setBasket={setBasket} />
        <Cartt basket={basket} setBasket={setBasket} />
        <Cartt basket={basket} setBasket={setBasket} />
        <Cartt basket={basket} setBasket={setBasket} />

    </Main>

    </Container>
  )
}


const Container = styled.div`
    width:100%;
    background-color:rgb(237,237,237);

`;
const Banner = styled.div`
    width:100%;
    img{
        width:100%;
        -webkit-mask-image:linear-gradient(
            to bottom,
            rgba(0,0,0,2),
            rgba(0,0,0,0.85),
            rgba(0,0,0,0.75),
            rgba(0,0,0,0.55),
            rgba(0,0,0,0)
            );

            &:nth-child(2) {
                display:none;
            }
            @media only screen and (max-width:767px){
                &:nth-child(1) {
                    display:none;
                }
                &:nth-child(2){
                    display:block;
                }
            }
    }

`;

const Main = styled.div`
    display:grid;
    justify-content:center;
    place-items:center;
    width:100%;
    
    grid-auto-rows:420px;
    grid-template-columns:repeat(4,280px);
    grid-gap:20px;


    // mobile version

    @media only screen and (max-width:767px){
        grid-template-columns:repeat(2,50%);
        grid-gap=0;
        margin-top:-300px;
        padding:10px 0px;
    }

    //tablet version

    @media only screen and (min-width:767px) and (max-width:1200px) {
        grid-template-columns:repeat(3,30%);
     
    }

    @media only screen and (min-width:767px){
        margin-top:-300px;
        padding:10px 0px;
    }
`;

export default Home
