import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import Navbar from './Navbar';
import axios from 'axios';
import { useStateValue } from '../StateProvider';

function Order() {

    const [{user}]=useStateValue();
    const [order,setOrder]=useState([]);

    useEffect(()=>{


        //in here we are getting order details check 
        //on(server and this page to get idea of how to get data)

        axios
        .post('./orders/get',{email:user.email})
        .then((res)=>setOrder(res.data));
    },[]);
  return (
    <Container>
      <Navbar/>
      <Main>
      <OrderContainer>
       <h2>your Order</h2>

       <OrderDetails>
            <addressComponent>
                <h4>shipping address</h4>

                    <div>
                        <p>NAME</p>
                        <p>A1</p>
                        <p>A2</p>
                        <p>A3</p>
                        <p>Phone</p>
                    </div>
            </addressComponent>

                <OrderBasket>
                <h4>Order</h4>
                <p>SubTotal : Rs <span>7878</span></p>
                <Product>

                         <Image>
                             <img src="img/e1.jpg" alt="" />
                        </Image>
                    <Disciption>
                     <h4>product name</h4>
                    <p>Rs.676767</p>
  
                    </Disciption>

                </Product>
                </OrderBasket>
       </OrderDetails>

      </OrderContainer>

      </Main>
    </Container>
  )
}


const Container = styled.div`
    width:100%;
    height:fit-content;
    max-width:1400px;
    margin:auto;
    background-color:rgb(234,237,237);
    
`;

const Main =styled.div`
    min-width:100vw;
    min-height:100vh;
    display:flex;
    justify-content:center;

`;

const OrderContainer=styled.div`
    padding:15px;
    
    background-color:#fff;
    width:95%;

    h2{
        font-weight:500;
        border-bottom:1px solid lightgray;
        padding-bottom:15px;
    }
`;

const OrderDetails =styled.div`
    border-bottom:1px solid lightgray;

`;

const addressComponent=styled.div`
    margin-top:20px;
    div{
        margin-top:10px;
        margin-left:10px;

    }

    p{
        margin-top:4px;
        font-size:14px;
    }
`;

const OrderBasket= styled.div`
    margin-top:20px;
    
    p{
        margin-left:15px;
        margin-top:15px;
        font-size:15px;
    }
    span{
        font-weight:600;

    }

`;

const Product = styled.div`
    display:flex;
    align-items:center;

`;

const Image =styled.div`
    flex:0.3;
    img{
        width:100%;
    }

    @media only screen and (max-width:1200px){
        img{
            width:70%;
        }
    }
`;

const Disciption = styled.div `
    flex:0.7;

    h4{
        font-size:18px;
        font-weight:600;

        @media only screen and (max-width:1200px){
            font-size:14px;
        }
    }
    p{
        font-weight:600;
        margin-top:10px;
    }

    button{
        background-color:transparent;
        color:#1384b4;
        border:none;
        outline:none;
        margin-top:10px;
        cursor:pointer;
        
    &:hover{
        text-decoration:underline;
    }
    }

`;
export default Order
