import React from 'react';
import styled from 'styled-components';
import Rating from '@material-ui/lab/Rating';
import { useStateValue } from '../StateProvider';

function Cartt({image,price,rating,title}) {

   const [{basket},dispatch] = useStateValue();

   const addToBasket = (e) =>{
    e.preventDefault();

    dispatch({
        type:'ADD_TO_BASKRT',
        item:{
            title,
            price,
            image,
            rating,
        }
    })
   }

  return (
    <Container>
        <Image>
        <img src={image} alt="" />
        </Image>
        <Discription>
        <h5>{title}</h5>
        <Rating name="half-rating-read" defaultValue={rating} precision={0.5} readOnly />
        <p>Rs : {price}</p>
        <button onClick={addToBasket}>Add to Cart</button>
        </Discription>
    </Container>
  )
}

const Container = styled.div`
    width:100%;
    height:100%;
    display:flex;
    flex-direction:column;
    background-color:#fff;
    z-index:10;
   
`;

const Image =styled.div`
    width:100%;

    display:flex;
    flex-direction:column;
    justify-content:center;
    align-items:center;
    margin-top:20px;
    flex:0.3;

    img{
        width:180px;
        height:200px;
    }
`;

const Discription =styled.div`
    width:90%;
    margin:auto;
    display:flex;
    flex-direction:column;
    justify-content:space-evenly;
    flex:0.7;

    h5{
        font-size:14px;
        font-weight:600;
      

    }
    p{
        font-weight:600;

    }
    button{
        width:100%;
        height:38px;
        background-color:#ffa533;
        border:none;
        border-radius:10px;
        cursor:pointer;
        // hover:lightyellow;
    }

    
    `;
export default Cartt
