import React, { useEffect } from 'react'
import styled from 'styled-components'
import Navbar from './Navbar';
import Cartt from './Cartt';
import axios from '../axios';


function Home({basket,setBasket}) {
    //useEffect use to run the data before rendering
    //useEffect runs every time the page refreash
    useEffect(()=>{
        const fetchdata = async () =>{
            const data = await axios.get('/products/get');
            console.log('products >>>>', data);
        };
        fetchdata();
    })

  return (
    <Container>
    <Navbar />
    <Banner>
        <img src="./img1/im6.jpg" alt="" />
        <img src="./img/e4.jpg"/>
        
    </Banner>

    <Main>
        <Cartt 
        id={1} 
        image={"https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/413jhqxM45L._SX300_SY300_QL70_FMwebp_.jpg"}
        price={8900}
        rating={2.5}
        title={"Mielle Organics Rosemary Mint"}
        />

        <Cartt 
        id={2} 
        image={"https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/31P-JgguucL._SX300_SY300_QL70_FMwebp_.jpg"}
        price={1200}
        rating={4.5}
        title={"Hydrocolloid Acne Pimple Patch"}
        />

         <Cartt
        id={3} 
        image={"https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/413jhqxM45L._SX300_SY300_QL70_FMwebp_.jpg"}
        price={8900}
        rating={4.5}
        title={"Mielle Organics Rosemary Mint"}
        />

         <Cartt
        id={4}  
        image={"https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/413jhqxM45L._SX300_SY300_QL70_FMwebp_.jpg"}
        price={8900}
        rating={2.5}
        title={"Mielle Organics Rosemary Mint"}
        />

         <Cartt
        id={5}   
        image={"https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/413jhqxM45L._SX300_SY300_QL70_FMwebp_.jpg"}
        price={8900}
        rating={2.5}
        title={"Mielle Organics Rosemary Mint"}
        />

         <Cartt
        id={6}  
        image={"https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/413jhqxM45L._SX300_SY300_QL70_FMwebp_.jpg"}
        price={8900}
        rating={2.5}
        title={"Mielle Organics Rosemary Mint"}
        />

         <Cartt
         id={7}   
        image={"https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/413jhqxM45L._SX300_SY300_QL70_FMwebp_.jpg"}
        price={8900}
        rating={2.5}
        title={"Mielle Organics Rosemary Mint"}
        />

         <Cartt
         id={8}  
        image={"https://m.media-amazon.com/images/W/IMAGERENDERING_521856-T1/images/I/413jhqxM45L._SX300_SY300_QL70_FMwebp_.jpg"}
        price={8900}
        rating={2.5}
        title={"Mielle Organics Rosemary Mint"}
        />


    </Main>

    </Container>
  )
}


const Container = styled.div`
    width:100%;
    background-color:rgb(237,237,237);

`;
const Banner = styled.div`
    width:100%;
  
   
        background-position: center;
    img{
        
        width:100%;
        -webkit-mask-image:linear-gradient(
            to bottom,
            rgba(0,0,0,1),
            rgba(0,0,0,0.85),
            rgba(0,0,0,0.75),
            rgba(0,0,0,0.55),
            rgba(0,0,0,0)
            );

            &:nth-child(2) {
                display:none;
            }
            @media only screen and (max-width:767px){
                &:nth-child(1) {
                    display:none;
                }
                &:nth-child(2){
                    display:block;
                }
            }
    }

`;

const Main = styled.div`
    display:grid;
    justify-content:center;
    place-items:center;
    width:100%;
    
    grid-auto-rows:420px;
    grid-template-columns:repeat(4,280px);
    grid-gap:20px;


    // mobile version

    @media only screen and (max-width:767px){
        grid-template-columns:repeat(2,50%);
        grid-gap=0;
        margin-top:-350px;
        padding:10px 0px;
    }

    //tablet version

    @media only screen and (min-width:767px) and (max-width:1200px) {
        grid-template-columns:repeat(3,30%);
     
    }

    @media only screen and (min-width:767px){
        margin-top:-300px;
        padding:10px 0px;
    }
`;

export default Home
