const reducer = (state, action ) => {
    console.log("action >>>>>", action);

    switch(action.type){
        case "ADD_TO_BASKET":
            return{
                ...state,
                basket: [...state.basket , action.item],
             };

        case "REMOVE_FROM_BASKET":
            const index =state.basket.findIndex(
